{"metadata":{"language_info":{"name":"python","version":"3.7.8","mimetype":"text/x-python","codemirror_mode":{"name":"ipython","version":3},"pygments_lexer":"ipython3","nbconvert_exporter":"python","file_extension":".py"},"kernelspec":{"name":"python3","display_name":"Python 3","language":"python"}},"nbformat_minor":5,"nbformat":4,"cells":[{"cell_type":"code","source":"def sieve(n):\n    prime = [True for i in range(n + 1)]\n    p = 2\n    while (p * p <= n):\n        if prime[p] == True:\n            for i in range(p * p, n + 1, p):\n                prime[i]= False\n        p += 1\n        \n    arr = []   \n    for i in range(1,n + 1):\n        if prime[i] == True:\n            arr.append(i)\n    return arr\n\n\nn = int(input())\nsieved = sieve(n)\n\nmid = len(sieved) // 2\n\nfor i in sieved[mid:len(sieved)]:\n    print(i, n - i)\n    break\n    ","metadata":{"trusted":true},"execution_count":30,"outputs":[{"output_type":"stream","name":"stdin","text":" 12\n"},{"name":"stdout","text":"5 7\n","output_type":"stream"}],"id":"7bcb95f2-6b9d-4195-b9c7-8c82de9473a5"},{"cell_type":"code","source":"","metadata":{},"execution_count":null,"outputs":[],"id":"590b5c47-8d2e-458b-beed-d73695cb753e"}]}